#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent  // 스크립트 파일의 디렉터리 경로 설정

$exe=""
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {  // PowerShell 버전이 6.0 미만이거나 Windows인 경우
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"  // 실행 파일 확장자 설정
}

$ret=0
if (Test-Path "$basedir/node$exe") {  // 경로에 node 실행 파일이 있는지 확인
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {  // 파이프라인 입력 지원
    $input | & "$basedir/node$exe"  "$basedir/../js-beautify/js/bin/css-beautify.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../js-beautify/js/bin/css-beautify.js" $args
  }
  $ret=$LASTEXITCODE  // 마지막 명령의 종료 코드 저장
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {  // 파이프라인 입력 지원
    $input | & "node$exe"  "$basedir/../js-beautify/js/bin/css-beautify.js" $args
  } else {
    & "node$exe"  "$basedir/../js-beautify/js/bin/css-beautify.js" $args
  }
  $ret=$LASTEXITCODE  // 마지막 명령의 종료 코드 저장
}
exit $ret  // 종료 코드 반환
