#!/usr/bin/env pwsh  // // PowerShell 스크립트를 실행할 때 사용할 인터프리터 지정
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent  // 스크립트 파일의 디렉토리 경로를 저장

$exe=""
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
}
$ret=0
if (Test-Path "$basedir/node$exe") {
  // "$basedir/node$exe" 경로가 존재하는지 확인

  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../js-beautify/js/bin/html-beautify.js" $args
  } else {
     // 입력을 받지 않는 경우
    & "$basedir/node$exe"  "$basedir/../js-beautify/js/bin/html-beautify.js" $args
  }
  $ret=$LASTEXITCODE   // 마지막으로 실행된 명령의 종료 코드 저장
} else {
   // "$basedir/node$exe" 경로가 존재하지 않는 경우
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../js-beautify/js/bin/html-beautify.js" $args
  } else {
    // 입력을 받지 않는 경우
    & "node$exe"  "$basedir/../js-beautify/js/bin/html-beautify.js" $args
  }
  $ret=$LASTEXITCODE  // 마지막으로 실행된 명령의 종료 코드 저장
}
exit $ret  // 종료 코드 반환
