/** 
 * @deprecated since v6.3.0 - use constants property exposed by the relevant module instead.
 */
declare module 'constants' { // 상수 모듈 선언
    import { constants as osConstants, SignalConstants } from 'node:os'; // 노드 OS 모듈에서 상수 가져오기
    import { constants as cryptoConstants } from 'node:crypto'; // 노드 암호화 모듈에서 상수 가져오기
    import { constants as fsConstants } from 'node:fs'; // 노드 파일 시스템 모듈에서 상수 가져오기

    const exp: typeof osConstants.errno & // OS 에러번호 및 우선순위 타입 가져오기
        typeof osConstants.priority & // OS 우선순위 타입 가져오기
        SignalConstants & // 시그널 상수 가져오기
        typeof cryptoConstants & // 암호화 상수 가져오기
        typeof fsConstants; // 파일 시스템 상수 가져오기
    export = exp; // 상수 내보내기
}

declare module 'node:constants' { // 노드 상수 모듈 선언
    import constants = require('constants'); // 상수 가져오기
    export = constants; // 상수 내보내기
}
