// 스트림 컨슈머 모듈을 선언하고 node:buffer에서 Blob을 가져와서 내보냄
// node:stream에서 Readable을 가져옴
declare module 'stream/consumers' {
    import { Blob as NodeBlob } from 'node:buffer';
    import { Readable } from 'node:stream';
    // 버퍼 컨슈머: Readable 스트림 또는 AsyncIterator에서 버퍼를 반환하는 프로미스
    function buffer(stream: NodeJS.ReadableStream | Readable | AsyncIterator<any>): Promise<Buffer>;
    // 텍스트 컨슈머: Readable 스트림 또는 AsyncIterator에서 텍스트를 반환하는 프로미스
    function text(stream: NodeJS.ReadableStream | Readable | AsyncIterator<any>): Promise<string>;
    // 배열 버퍼 컨슈머: Readable 스트림 또는 AsyncIterator에서 배열 버퍼를 반환하는 프로미스
    function arrayBuffer(stream: NodeJS.ReadableStream | Readable | AsyncIterator<any>): Promise<ArrayBuffer>;
    // 블롭 컨슈머: Readable 스트림 또는 AsyncIterator에서 블롭을 반환하는 프로미스
    function blob(stream: NodeJS.ReadableStream | Readable | AsyncIterator<any>): Promise<NodeBlob>;
    // JSON 컨슈머: Readable 스트림 또는 AsyncIterator에서 JSON을 반환하는 프로미스
    function json(stream: NodeJS.ReadableStream | Readable | AsyncIterator<any>): Promise<unknown>;
}

// node:stream/consumers 모듈을 선언하고 stream/consumers로부터 내보냄
declare module 'node:stream/consumers' {
    export * from 'stream/consumers';
}
