var parsePath = require('./lib/parse-path') // parsePath 함수 불러오기
var setValue = require('./lib/set-value') // setValue 함수 불러오기

function appendField (store, key, value) {
  var steps = parsePath(key) // 키를 파싱하여 스텝 생성

  steps.reduce(function (context, step) { // 생성된 스텝에 따라 값 추가
    return setValue(context, step, context[step.key], value)
  }, store)
}

module.exports = appendField // appendField 함수를 모듈로 내보내기
