/* eslint-env mocha */

var assert = require('assert')
var appendField = require('../') // appendField 함수 불러오기
var testData = require('testdata-w3c-json-form') // 테스트 데이터 불러오기

describe('Append Field', function () {
  for (var test of testData) { // 테스트 데이터에 대해 반복
    it('handles ' + test.name, function () { // 각 테스트에 대한 설명
      var store = Object.create(null) // 빈 객체 생성

      for (var field of test.fields) { // 각 테스트의 필드에 대해 반복
        appendField(store, field.key, field.value) // appendField 함수 호출
      }

      assert.deepEqual(store, test.expected) // 결과 검증
    })
  }
})
