module.exports = r => { // 모듈 내보내기 함수 정의
  const n = process.versions.node.split('.').map(x => parseInt(x, 10)) // 현재 Node.js 버전을 배열로 변환
  r = r.split('.').map(x => parseInt(x, 10)) // 비교할 버전을 배열로 변환
  return n[0] > r[0] || (n[0] === r[0] && (n[1] > r[1] || (n[1] === r[1] && n[2] >= r[2]))) // 현재 버전이 비교할 버전보다 크거나 같은지 여부를 반환
}
